$(document).ready(function(){$(".quote-form").on("submit",function(e){e.preventDefault();const o=$("#quote-author").val(),t=$("#quote").val(),r=$("#quote-source").val(),u=$("#quote-url").val();$.ajax({method:"post",url:red_vars.rest_url+"wp/v2/posts",data:{title:o,content:t,_qod_quote_source:r,_qod_quote_source_url:u},beforeSend:function(e){e.setRequestHeader("X-WP-Nonce",red_vars.wpapi_nonce)}}).done(function(e){alert("Success! Comments are closed for this post.")})})}),$(".quotes-button").on("click",function(){$.ajax({method:"GET",url:red_vars.rest_url+"wp/v2/posts?filter[orderby]=rand&filter[posts_per_page]=1"}).done(e=>{let o=e[0].content.rendered,t=e[0].title.rendered,r=e[0]._qod_quote_source,u=e[0]._qod_quote_source_url;$(".quotes-content").html(o),$(".quote-author").html("- "+t),r?$(".quote-source").html(u?", <a href='"+u+"'>"+r+"</a>":", "+r):$(".quote-source").html("")}).fail(function(e){console.log(e)})});